    private void sendMoneyToUserAccount() {
        DecimalFormat decimalFormat = new DecimalFormat("0.00");
        my_pen_account_double = Double.parseDouble(basic_account_pen);
        my_usd_account_double = Double.parseDouble(basic_account_usd);
        double new_value_pen_account, new_value_usd_account;
        HashMap accountHashMap = new HashMap();
        if (account_currency.equals("USD"))
        {
            new_value_pen_account = my_pen_account_double+amount_double;
            String my_money_pen_now = decimalFormat.format(new_value_pen_account);
            accountHashMap.put("current_account_usd",my_money_pen_now);

        }
        if (account_currency.equals("PEN"))
        {
            double my_real_ammount_usd = amount_double*currrency_sell_rate;
            new_value_usd_account = my_usd_account_double+my_real_ammount_usd;
            String my_money_usd_now = decimalFormat.format(new_value_usd_account);
            accountHashMap.put("current_account_usd",my_money_usd_now);
        }

        userRef.child(currentUserID).updateChildren(accountHashMap).addOnCompleteListener(new OnCompleteListener() {
            @Override
            public void onComplete(@NonNull Task task) {
                if (task.isSuccessful())
                {
                    registerOperation();
                }
            }
        });
    }

